import { Image } from "cloudinary-react";
import React, { useState } from "react";
import useNativeLazyLoading from "@charlietango/use-native-lazy-loading";
import { useInView } from "react-intersection-observer";

// This is a wrapper for cloudynary Image handler
// Could be replace by any asset provider

export default ({ ...rest }) => {
  let initialProps = { ...rest };

  const supportsLazyLoading = useNativeLazyLoading();
  const [ref, inView] = useInView({
    triggerOnce: true,
    rootMargin: "200px 0px",
  });

  return (
    <div className="relative block overflow-hidden transition-all ease-in">
      {inView || supportsLazyLoading ? <Image {...initialProps} /> : null}
    </div>
  );
};
