import { Image } from "cloudinary-react";
import React, { useRef, useEffect, useState } from "react";

// This is a wrapper for cloudynary Image handler
// Could be replace by any asset provider

export default ({ ...rest }) => {
  const [isLoading, setIsLoading] = useState(true);
  const imageRef = useRef(null);
  const initialProps = { ...rest, quality: "10" };

  useEffect(() => {
    initialProps.quality = 100;
    // setIsLoading(false);
  }, [imageRef]);

  return (
    <div
      className="relative overflow-hidden"
      style={isLoading ? { filter: "blur(10px)" } : {}}
    >
      <Image ref={imageRef} {...initialProps} />
    </div>
  );
};
