import { Image } from "cloudinary-react";
import React, { useRef, useEffect, useState } from "react";
import { InView } from "react-intersection-observer";

// This is a wrapper for cloudynary Image handler
// Could be replace by any asset provider

export default ({ ...rest }) => {
  const [isLoading, setIsLoading] = useState(true);
  let initialProps = { ...rest };

  // useEffect(() => {
  //   setTimeout(() => {
  //     initialProps.quality = 100;
  //     setIsLoading(false);
  //   }, 1000);
  // }, [imageRef]);

  return (
    <InView
      as="div"
      onChange={(inView, entry) => console.log("Inview:", inView)}
    >
      <div
        className="relative block overflow-hidden transition-all ease-in"
        style={isLoading ? { filter: "blur(10px)" } : {}}
      >
        <Image ref={imageRef} {...initialProps} />
      </div>
    </InView>
  );
};
